import React, { useEffect, useState } from 'react'
import './App.css'
export default function App() {
  const [a, setA] = useState([]);
  const [count, setCount] = useState(0);
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [state, setState] = useState('');


  useEffect(() => {
    fetch('http://localhost:8081/cddemo')
      .then(res => res.json())
      .then(data => { setA(data); })

  }, [a])

  const handleCount = () => {
    fetch('http://localhost:8081/count')
      .then(res => res.json())
      .then(data => { setCount(a.length); })
  }

  const insertrecord = () => {
    fetch('http://localhost:8081/insert')
      .then(res => res.json())
      .then(data => { setA(prev => [...prev, data]); })
      .then(req => req.body(name, address, state))
  }
  return (
    <div>
      <table style={{ border: '1px solid black' }}>
        <tr>
          <th>ID</th>
          <th>NAME</th>
          <th>STATE</th>
          <th>ADDRESS</th>
        </tr>

        {a.map((p, index) => (<>

          <tr>
            <td>{p.id}</td>
            <td>{p.name}</td>
            <td>{p.state}</td>
            <td>{p.address}</td>
          </tr>
        </>))}
      </table>
      <button onClick={handleCount}>Count records</button>
      Name : <input type="text" placeholder='Enter name' onChange={(e) => {
        setName(e.target.value)
      }} />
      Address : <input type="text" placeholder='Enter name' onChange={(e) => {
        setAddress(e.target.value)
      }} />
      State : <input type="text" placeholder='Enter name' onChange={(e) => {
        setState(e.target.value)
      }} />
      <button onClick={insertrecord}>Insert</button>
      <p>Records : {count}</p>
    </div>
  )
}
