import React, { useEffect, useState } from 'react'
import './App.css'
import Axios from 'axios';


function CRUD() {

    const [a, setA] = useState([]);
    const [count, setCount] = useState(0);
    const [name, setName] = useState('');
    const [address, setAddress] = useState('');
    const [state, setState] = useState('');
    const [id, setId] = useState('');
    const [showUpdateForm, setShowUpdateForm] = useState(false);

    useEffect(() => {
        fetch('http://localhost:8081/cddemo')
            .then(res => res.json())
            .then(data => { setA(data); })

    }, [a])

    const handleCount = () => {
        fetch('http://localhost:8081/count')
            .then(res => res.json())
            .then(data => { setCount(a.length); })
    }

    const insertrecord = () => {
        console.log(name, address, state);
        Axios.post('http://localhost:8081/insert', { name: name, address: address, state: state })
            .then(response => {
                console.log('Record inserted successfully');
            })
            .catch(error => {
                console.error('Error inserting record:', error);
            });

    }

    const updaterecord = (id) => {
        console.log(name, address, state);
        Axios.post('http://localhost:8081/update/' + `${id}`, { name: name, address: address, state: state, id: id })
            .then(response => {
                console.log('Record updated successfully');
            })
            .catch(error => {
                console.error('Error inserting record:', error);
            });

    }
    return (
        <div>
            <br></br>
            <table style={{ border: '1px solid black', borderCollapse: 'collapse', marginLeft: "30px" }}>
                <tr>
                    <th style={{ border: '1px solid black', borderCollapse: 'collapse' }}>ID</th>
                    <th style={{ border: '1px solid black', borderCollapse: 'collapse' }}>NAME</th>
                    <th style={{ border: '1px solid black', borderCollapse: 'collapse' }}>STATE</th>
                    <th style={{ border: '1px solid black', borderCollapse: 'collapse' }}>ADDRESS</th>
                    <th style={{ border: '1px solid black', borderCollapse: 'collapse' }}>ACTION</th>

                </tr>

                {a.map((p, index) => (<>

                    <tr>
                        <td style={{ border: '1px solid black', borderCollapse: 'collapse' }}>{p.id}</td>
                        <td style={{ border: '1px solid black', borderCollapse: 'collapse' }}>{p.name}</td>
                        <td style={{ border: '1px solid black', borderCollapse: 'collapse' }}>{p.state}</td>
                        <td style={{ border: '1px solid black', borderCollapse: 'collapse' }}>{p.address}</td>
                        <td>
                            <button onClick={() => setShowUpdateForm(!showUpdateForm)}>Update record</button>
                            {
                                showUpdateForm ? (
                                    <td><form>
                                        <label>Name : </label>
                                        <input type='text' id='name' name='name' onChange={(e) => setName(e.target.value)} /><br />

                                        <label>Country : </label>
                                        <input type='text' id='country' name='country' onChange={(e) => setAddress(e.target.value)} /><br />

                                        <label>Age : </label>
                                        <input type='text' id='age' name='age' onChange={(e) => setState(e.target.value)} /><br />

                                        <button onClick={() => updaterecord(p.id)}>submit</button>
                                    </form></td>
                                ) : (<></>)
                            }
                        </td>
                    </tr>



                </>))}
            </table>
            <br></br>
            <button onClick={handleCount}>Count records</button><p>Records : {count}</p>
            <br></br>
            Name : <input type="text" placeholder='Enter name' onChange={(e) => {
                setName(e.target.value)
            }} /><br></br>
            Address : <input type="text" placeholder='Enter address' onChange={(e) => {
                setAddress(e.target.value)
            }} /><br></br>
            State : <input type="text" placeholder='Enter state' onChange={(e) => {
                setState(e.target.value)
            }} /><br></br>
            <button onClick={insertrecord}>Insert</button><br></br>


        </div >
    )
}

export default CRUD
