import React, { useEffect, useState } from 'react'
import './App.css'
import Axios from 'axios';


function CRUD() {

    const [a, setA] = useState([]);
    const [count, setCount] = useState(0);
    const [name, setName] = useState('');
    const [address, setAddress] = useState('');
    const [state, setState] = useState('');
    const [id, setId] = useState('');

    useEffect(() => {
        fetch('http://localhost:8081/cddemo')
            .then(res => res.json())
            .then(data => { setA(data); })

    }, [a])

    const handleCount = () => {
        fetch('http://localhost:8081/count')
            .then(res => res.json())
            .then(data => { setCount(a.length); })
    }

    const insertrecord = () => {
        console.log(name, address, state);
        Axios.post('http://localhost:8081/insert', { name: name, address: address, state: state })
            .then(response => {
                console.log('Record inserted successfully');
            })
            .catch(error => {
                console.error('Error inserting record:', error);
            });

    }

    const updaterecord = () => {
        console.log(name, address, state);
        Axios.post('http://localhost:8081/update', { name: name, address: address, state: state, id: id })
            .then(response => {
                console.log('Record inserted successfully');
            })
            .catch(error => {
                console.error('Error inserting record:', error);
            });

    }
    return (
        <div>
            <table style={{ border: '1px solid black' }}>
                <tr>
                    <th>ID</th>
                    <th>NAME</th>
                    <th>STATE</th>
                    <th>ADDRESS</th>
                </tr>

                {a.map((p, index) => (<>

                    <tr>
                        <td>{p.id}</td>
                        <td>{p.name}</td>
                        <td>{p.state}</td>
                        <td>{p.address}</td>
                    </tr>
                </>))}
            </table>
            <button onClick={handleCount}>Count records</button>
            Name : <input type="text" placeholder='Enter name' onChange={(e) => {
                setName(e.target.value)
            }} />
            Address : <input type="text" placeholder='Enter address' onChange={(e) => {
                setAddress(e.target.value)
            }} />
            State : <input type="text" placeholder='Enter state' onChange={(e) => {
                setState(e.target.value)
            }} />
            <button onClick={insertrecord}>Insert</button>
            <p>Records : {count}</p>
        </div>
    )
}

export default CRUD
